facility.dropdown:
query FacilityDropdown {
  options: facilities {
    key
    value
    label
  }
}

facility.insertOne:
mutation FacilityInsert($data: FacilityInsertInput!) {
	inserted: insertOneFacility(data: $data) {
        _id
name @client
phone
email
facilityNumber
selfStorage {
_id
name
}
address {
street
suite
city
state
country
postalCode
}

  }  
}

facility.selectAll:
query FacilitiesGridAll {
      grid: facilities(sortBy: SELFSTORAGE_ASC) {
          _id
name @client
phone
email
facilityNumber
selfStorage {
_id
name
}
address {
street
suite
city
state
country
postalCode
}

      }
  }

facility.selectOne:
query FacilityGridOne($query: FacilityQueryInput!) {
	record: facility(query: $query) {
    _id
name @client
phone
email
facilityNumber
selfStorage {
_id
name
}
address {
street
suite
city
state
country
postalCode
}

  }
}

facility.updateOne:
mutation FacilityUpdate($id: ObjectId!, $set: FacilityUpdateInput!) {
  record: updateOneFacility(query: { _id: $id }, set: $set) {
    _id
    _id
name @client
phone
email
facilityNumber
selfStorage {
_id
name
}
address {
street
suite
city
state
country
postalCode
}

  }
}

